// Pobierz oryginalnƒÖ listƒô news√≥w
const input = $input.first().json;
const claudeRaw = input.claudeResponse;
const origItems = input.originalItems;

// Pobierz analizƒô Claude
let analysis = [];

try {
  // Claude zwraca content jako tablicƒô ‚Äî wyciƒÖgamy tekst
  const rawText = claudeRaw.content?.[0]?.text || '[]';
  // Usu≈Ñ ewentualne backticki gdyby Claude jednak doda≈Ç markdown
  const cleaned = rawText.replace(/```json|```/g, '').trim();
  analysis = JSON.parse(cleaned);
} catch(e) {
  // Je≈õli parsowanie padnie ‚Äî kontynuuj bez sentymentu
  analysis = [];
}

// Stw√≥rz mapƒô: id ‚Üí analiza (id w Claude zaczyna siƒô od 1)
const analysisMap = {};
for (const a of analysis) {
  analysisMap[a.id] = a;
}

// Sta≈Çe wizualne
const sentimentIcon = { BULLISH: 'üìà', BEARISH: 'üìâ', NEUTRAL: '‚û°Ô∏è' };
const importanceDot = { HIGH: 'üî¥', MEDIUM: 'üü°', LOW: '‚ö™' };
const tickerColor   = '#2980b9';

// Podziel na kategorie
const highItems  = origItems.filter((_, i) => analysisMap[i+1]?.importance === 'HIGH');
const spolkiRest = origItems.filter((item, i) => 
  item._category === 'SPOLKA' && analysisMap[i+1]?.importance !== 'HIGH'
);
const makroItems = origItems.filter((item, i) => 
  item._category === 'MAKRO' && analysisMap[i+1]?.importance !== 'HIGH'
);

const renderRow = (item, idx) => {
  const a       = analysisMap[idx + 1] || {};
  const date    = item.pubDate
    ? new Date(item.pubDate).toLocaleString('pl-PL', {
        day: '2-digit', month: '2-digit',
        hour: '2-digit', minute: '2-digit'
      })
    : '';
  const ticker  = item._ticker || '';
  const title   = item.title || '';
  const sourceBadge = item._source === 'google_news' 
  ? `<span style="color:#aaa; font-size:10px;"> ‚Ä¢ Google News</span>`
  : `<span style="color:#aaa; font-size:10px;"> ‚Ä¢ Bankier</span>`;
  const link    = item.link || '#';
  const icon    = sentimentIcon[a.sentiment] || '‚û°Ô∏è';
  const dot     = importanceDot[a.importance] || '‚ö™';
  const reason  = a.reason || '';

  return `<tr style="border-bottom:1px solid #f0f0f0;">
    <td style="padding:6px 8px; color:#999; font-size:11px; white-space:nowrap; vertical-align:top;">${date}</td>
    <td style="padding:6px 8px; vertical-align:top;">
      <span style="background:${tickerColor}; color:white; border-radius:3px; padding:1px 5px; font-size:11px; margin-right:4px;">${ticker}</span>
      ${icon} ${dot}
      <a href="${link}" style="color:#2c3e50; text-decoration:none; font-weight:500;">${title} ${sourceBadge}</a>
      ${reason ? `<br><span style="color:#888; font-size:12px; margin-left:4px;">‚Ü≥ ${reason}</span>` : ''}
    </td>
  </tr>`;
};

// Zlicz statystyki
const bullish = analysis.filter(a => a.sentiment === 'BULLISH').length;
const bearish = analysis.filter(a => a.sentiment === 'BEARISH').length;
const high    = analysis.filter(a => a.importance === 'HIGH').length;

const html = `
<html><body style="font-family:Arial,sans-serif; max-width:720px; margin:auto; color:#333;">

<h2 style="border-bottom:2px solid #2980b9; padding-bottom:8px;">
  üìä Raport GPW ‚Äî ${new Date().toLocaleDateString('pl-PL', {
    weekday:'long', day:'numeric', month:'long'
  })}
</h2>

<div style="background:#f8f9fa; border-radius:6px; padding:10px 14px; margin-bottom:16px; font-size:13px;">
  üìà Bullish: <strong>${bullish}</strong> &nbsp;|&nbsp;
  üìâ Bearish: <strong>${bearish}</strong> &nbsp;|&nbsp;
  üî¥ Wa≈ºne: <strong>${high}</strong> &nbsp;|&nbsp;
  ≈ÅƒÖcznie: <strong>${origItems.length}</strong> news√≥w
</div>

${highItems.length > 0 ? `
<h3 style="color:#c0392b;">üî¥ Wysokie znaczenie ‚Äî mo≈ºliwy wp≈Çyw na kurs</h3>
<table style="width:100%; border-collapse:collapse;">
  <tbody>${highItems.map((item) => renderRow(item, origItems.indexOf(item))).join('')}</tbody>
</table>
` : ''}

${spolkiRest.length > 0 ? `
<h3 style="color:#2c3e50; margin-top:20px;">üè¢ Twoje sp√≥≈Çki</h3>
<table style="width:100%; border-collapse:collapse;">
  <tbody>${spolkiRest.map((item) => renderRow(item, origItems.indexOf(item))).join('')}</tbody>
</table>
` : ''}

${makroItems.length > 0 ? `
<h3 style="color:#7f8c8d; margin-top:20px;">üåç Makroekonomia</h3>
<table style="width:100%; border-collapse:collapse;">
  <tbody>${makroItems.map((item) => renderRow(item, origItems.indexOf(item))).join('')}</tbody>
</table>
` : ''}

<hr style="margin-top:24px;">
<p style="color:#bbb; font-size:11px;">
  n8n GPW Agent v3.0 | Analiza: Claude Haiku | ≈πr√≥d≈Ça: Bankier ESPI + Gie≈Çda
</p>
</body></html>
`;

return [{ json: { 
  html, 
  count: origItems.length, 
  high, 
  bullish, 
  bearish 
}}];

// ============================================
// TWOJE PORTFOLIO ‚Äî dostosuj nazwy do swoich sp√≥≈Çek
// Ka≈ºda sp√≥≈Çka ma listƒô s≈Ç√≥w kluczowych kt√≥re
// mogƒÖ pojawiƒá siƒô w tytule newsa
// ============================================
const PORTFOLIO = [
  { ticker: 'ICE',  keywords: ['Medinice', 'ICE', 'medinice'] },
  { ticker: 'OPL',  keywords: ['Orange Polska', 'Orange PL', 'OPL'] },
  { ticker: 'PKN',  keywords: ['PKN', 'Orlen', 'ORLEN', 'PKN ORLEN'] },
  { ticker: 'KGH',  keywords: ['KGHM', 'KGH', 'Polska Mied≈∫'] },
  { ticker: 'JSW',  keywords: ['JSW', 'Jastrzƒôbska', 'Jastrzebska'] },
  // Dodaj pozosta≈Çe sp√≥≈Çki z Twojego portfolio:
  { ticker: 'LBW',  keywords: ['Lubawa', 'LBW'] },
  { ticker: 'MLR',  keywords: ['Molecure', 'MLR'] },
  { ticker: 'ENA',  keywords: ['Enea', 'ENA'] },
  { ticker: 'PGE',  keywords: ['PGE'] },
  { ticker: 'TRK',  keywords: ['Trakcja', 'TRK'] },
  { ticker: 'DVL',  keywords: ['Develia', 'DVL'] },
  { ticker: 'CPT',  keywords: ['Captor', 'CPT'] },
  { ticker: 'ALR',  keywords: ['Alior', 'ALR'] },
  { ticker: 'CRE',  keywords: ['Creotech', 'CRE'] },
  { ticker: 'PZU',  keywords: ['PZU'] },
  { ticker: 'ATT',  keywords: ['Azoty', 'ATT', 'Grupa Azoty'] },
];

// ============================================
// MAKRO ‚Äî newsy kt√≥re zawsze przepuszczamy
// niezale≈ºnie od sp√≥≈Çki
// ============================================
const MAKRO_KEYWORDS = [
  'RPP', 'stopy procentowe', 'stopa procentowa',
  'inflacja', 'CPI', 'GUS', 'NBP',
  'Fed', 'Federal Reserve', 'EBC', 'ECB',
  'PKB', 'GDP', 'bezrobocie',
  'c≈Ça', 'taryfy', 'Trump',
  'recesja', 'stagflacja',
  'WIG20', 'WIG', 'GPW sesja',
];

// ============================================
// LOGIKA FILTROWANIA
// ============================================
const items = $input.all().filter(i => !i.json._skip);
const result = [];

for (const item of items) {
  const title = (item.json.title || '').toLowerCase();
  const desc  = (item.json.content || item.json.description || '').toLowerCase();
  const text  = title + ' ' + desc;

  // Sprawd≈∫ czy news dotyczy sp√≥≈Çki z portfolio
  let matchedTicker = null;
  for (const stock of PORTFOLIO) {
    if (stock.keywords.some(kw => text.includes(kw.toLowerCase()))) {
      matchedTicker = stock.ticker;
      break;
    }
  }

  // Sprawd≈∫ czy to news makro
  const isMakro = MAKRO_KEYWORDS.some(kw => text.includes(kw.toLowerCase()));

  // Przepu≈õƒá tylko je≈õli pasuje do sp√≥≈Çki LUB jest makro
  if (matchedTicker || isMakro) {
    result.push({
      json: {
        ...item.json,
        _ticker: matchedTicker || 'MAKRO',
        _category: matchedTicker ? 'SPOLKA' : 'MAKRO',
      }
    });
  }
}

// Posortuj: najpierw sp√≥≈Çki, potem makro
result.sort((a, b) => {
  if (a.json._category === b.json._category) return 0;
  return a.json._category === 'SPOLKA' ? -1 : 1;
});
const seen = new Set();
const deduped = result.filter(item => {
  // Klucz: pierwsze 50 znak√≥w tytu≈Çu (normalizowane)
  const key = (item.json.title || '').toLowerCase().substring(0, 50);
  if (seen.has(key)) return false;
  seen.add(key);
  return true;
});

return deduped;

const items = $input.all();

// Zbuduj listƒô news√≥w jako tekst dla Claude
const newsText = items.map((item, i) => {
  const ticker   = item.json._ticker || 'MAKRO';
  const category = item.json._category || 'MAKRO';
  const title    = item.json.title || '';
  const desc     = item.json.content || item.json.description || '';
  // Ucinamy opis do 200 znak√≥w ≈ºeby nie przepalaƒá token√≥w
  const shortDesc = desc.replace(/<[^>]*>/g, '').substring(0, 200);

  return `${i + 1}. [${ticker}] ${title}${shortDesc ? '\n   ' + shortDesc : ''}`;
}).join('\n\n');

// Zbuduj gotowy obiekt requestu dla Claude
const requestBody = {
  model: "claude-haiku-4-5-20251001",
  max_tokens: 2000,
  system: "Jeste≈õ analitykiem rynku GPW i makroekonomii. Analizujesz newsy pod kƒÖtem wp≈Çywu na kursy akcji polskich sp√≥≈Çek. Odpowiadasz WY≈ÅƒÑCZNIE w formacie JSON ‚Äî bez ≈ºadnego tekstu przed ani po JSON. Bez markdown, bez komentarzy.",
  messages: [
    {
      role: "user",
      content: `Przeanalizuj poni≈ºsze newsy gie≈Çdowe. Dla ka≈ºdego zwr√≥ƒá obiekt JSON z polami:\n- id: numer newsa (liczba)\n- sentiment: BULLISH / BEARISH / NEUTRAL\n- importance: HIGH / MEDIUM / LOW\n- reason: max 15 s≈Ç√≥w po polsku ‚Äî dlaczego taki wp≈Çyw na kurs\n\nKryterium importance:\n- HIGH: wyniki finansowe, du≈ºe kontrakty, zmiana zarzƒÖdu, postƒôpowania regulacyjne, decyzje RPP\n- MEDIUM: mniejsze kontrakty, prognozy analityk√≥w, dane makro\n- LOW: formalne komunikaty, transakcje insider√≥w na ma≈ÇƒÖ skalƒô, WZA\n\nOdpowiedz TYLKO tablicƒÖ JSON, np.:\n[{"id":1,"sentiment":"BULLISH","importance":"HIGH","reason":"Wyniki znacznie powy≈ºej oczekiwa≈Ñ rynku"}]\n\nNewsy do analizy:\n${newsText}`
    }
  ]
};
// Zapisz originalItems w Static Data ≈ºeby by≈Çy dostƒôpne po HTTP Request
const staticData = $getWorkflowStaticData('global');
staticData.originalItems = items.map(i => i.json);

// Zwr√≥ƒá jeden item z ca≈Çym tekstem + oryginalnƒÖ listƒÖ news√≥w
return [{
  json: {
    newsText,
    requestBody,
    originalItems: items.map(i => i.json),
    count: items.length
  }
}];

