// Dane z Claude (poprzedni node)
const claudeResponse = $input.first().json;

// originalItems przekazane przez HTTP Request nie sƒÖ dostƒôpne
// Dlatego rekonstruujemy je z danych kt√≥re HTTP Request otrzyma≈Ç
// poprzez zapisanie ich w Static Data w Code: Przygotuj prompt

const stored = $getWorkflowStaticData('global');
const originalItems = stored.originalItems || [];

return [{
  json: {
    claudeResponse,
    originalItems
  }
}];

// Lista sp√≥≈Çek ‚Äî ta sama co w Code: Filtr
// Tutaj u≈ºywamy nazw do wyszukiwania w Google News
const spolki = [
  { ticker: 'ICE',  query: 'Medinice akcje GPW' },
  { ticker: 'OPL',  query: 'Orange Polska akcje GPW' },
  { ticker: 'PKN',  query: 'PKN Orlen akcje GPW' },
  { ticker: 'KGH',  query: 'KGHM akcje GPW' },
  { ticker: 'JSW',  query: 'JSW Jastrzƒôbska akcje GPW' },
  { ticker: 'LBW',  query: 'Lubawa akcje GPW' },
  { ticker: 'MLR',  query: 'Molecure akcje GPW' },
  { ticker: 'ENA',  query: 'Enea akcje GPW' },
  { ticker: 'PGE',  query: 'PGE akcje GPW' },
  { ticker: 'TRK',  query: 'Trakcja PRKiI akcje GPW' },
  { ticker: 'DVL',  query: 'Develia akcje GPW' },
  { ticker: 'CPT',  query: 'Captor Therapeutics akcje GPW' },
  { ticker: 'ALR',  query: 'Alior Bank akcje GPW' },
  { ticker: 'CRE',  query: 'Creotech Instruments akcje GPW' },
  { ticker: 'PZU',  query: 'PZU akcje GPW' },
  { ticker: 'ATT',  query: 'Grupa Azoty akcje GPW' },
  // Sp√≥≈Çki US
  { ticker: 'PLTR', query: 'Palantir stock news' },
  { ticker: 'QCOM', query: 'Qualcomm stock news' },
  { ticker: 'JPM',  query: 'JPMorgan stock news' },
];

return spolki.map(s => ({
  json: {
    ticker: s.ticker,
    url: `https://news.google.com/rss/search?q=${encodeURIComponent(s.query)}&hl=pl&gl=PL&ceid=PL:pl`
  }
}));

const items = $input.all();
const results = [];

for (const item of items) {
  const ticker = item.json.ticker || '';
  const xml = item.json.data || '';

  if (typeof xml !== 'string') continue;

  // WyciƒÖgnij wszystkie <item> z RSS
  const itemMatches = xml.match(/<item>([\s\S]*?)<\/item>/g) || [];

  for (const rawItem of itemMatches.slice(0, 5)) { // max 5 news√≥w per sp√≥≈Çka

    const getTag = (tag) => {
      const m = rawItem.match(new RegExp(`<${tag}[^>]*><!\\[CDATA\\[([\\s\\S]*?)\\]\\]><\\/${tag}>|<${tag}[^>]*>([^<]*)<\\/${tag}>`));
      return m ? (m[1] || m[2] || '').trim() : '';
    };

    const title   = getTag('title');
    const link    = getTag('link') || rawItem.match(/<link[^>]*>([^<]*)/)?.[1]?.trim() || '';
    const pubDate = getTag('pubDate');
    const source  = getTag('source');

    if (!title) continue;

    // Filtruj tylko newsy z ostatnich 48h
    const pub = new Date(pubDate);
    const cutoff = new Date(Date.now() - 48 * 60 * 60 * 1000);
    if (pubDate && pub < cutoff) continue;

    results.push({
      json: {
        title,
        link,
        pubDate,
        source,
        _ticker: ticker,
        _category: 'SPOLKA',
        _source: 'google_news'
      }
    });
  }
}

return results.length > 0 ? results : [{ json: { _skip: true } }];
